  #
  def test_cdatasectiongetdata
    doc = nil
    nameList = nil
    child = nil
    lastChild = nil
    data = nil
    nodeType = nil
    doc = load_document("staff", false)
      nameList = doc.getElementsByTagName("name")
      child = nameList.item(1)
