  def test_characterdatasetnodevalue
    doc = nil
    elementList = nil
    nameNode = nil
    child = nil
    childData = nil
    childValue = nil
    doc = load_document("staff", true)
      elementList = doc.getElementsByTagName("name")
      nameNode = elementList.item(0)
      child = nameNode.firstChild()
      child.nodeValue = "Marilyn Martin"
      childData = child.data()
      assert_equal("Marilyn Martin", childData, "data")
            childValue = child.nodeValue()
      assert_equal("Marilyn Martin", childValue, "value")
            
