    ##
    contentType = getContentType()
    preload(contentType, "staff", true)
  end

  ###
  # Runs the test case.
  # @throws Throwable Any uncaught exception causes test to fail
  #
  def test_processinginstructionsetdatanomodificationallowederrEE
    doc = nil
    genderList = nil
    gender = nil
    entRef = nil
    piNode = nil
    appendedChild = nil
    doc = load_document("staff", true)
      genderList = doc.getElementsByTagName("gender")
      gender = genderList.item(2)
      entRef = doc.createEntityReference("ent4")
      appendedChild = gender.appendChild(entRef)
      entRef = gender.lastChild()
      assert_not_nil(entRef, "entRefNotNull")
      piNode = entRef.lastChild()
      assert_not_nil(piNode, "piNodeNotNull")
      
    begin
      success = false;
      begin
        piNode.data = "newData"
      rescue Taka::DOMException => ex
        success = (ex.code == Taka::DOMException::NO_MODIFICATION_ALLOWED_ERR)
      end 
      assert(success, "throw_NO_MODIFICATION_ALLOWED_ERR")
    end

  end

