DOMTestCase('HTMLHtmlElement01') do

  ###
  # Constructor.
  # @param factory document factory, may not be null
  # @throws org.w3c.domts.DOMTestIncompatibleException Thrown if test is not compatible with parser configuration
  ##
  def setup
    ##
    ##   check if loaded documents are supported for content type
    ##
    contentType = getContentType()
    preload(contentType, "html", false)
  end

  ###
  # Runs the test case.
  # @throws Throwable Any uncaught exception causes test to fail
  #
  def test_HTMLHtmlElement01
    nodeList = nil
    testNode = nil
    vversion = nil
    doc = nil
    doc = load_document("html", false)
      nodeList = doc.getElementsByTagName("html")
      assertSize("Asize", 1, nodeList)
      testNode = nodeList.item(0)
      vversion = testNode.version()
      
      if (("text/html" == getContentType()))
        assert_equal("-//W3C//DTD HTML 4.01 Transitional//EN", vversion, "versionLink")
            
         end
       
  end

